def Buy(tokenAddress, nonce):
    if (tokenAddress != None):
        print(style.GREEN + 'Buying token')
        tokenToBuy = web3.toChecksumAddress(tokenAddress)
        spend = web3.toChecksumAddress("0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c")  # wbnb contract address
        contract = web3.eth.contract(address=pancakeSwapRouterAddress, abi=pancakeABI)
        pancakeswap2_txn = contract.functions.swapExactETHForTokens(
            0,
            # Set to 0 or specify min number of tokens - setting to 0 just buys X amount of token at its current price for whatever BNB specified
            [spend, tokenToBuy],
            walletAddress,
            (int(time.time()) + transactionRevertTime)
        ).buildTransaction({
            'from': walletAddress,
            'value': web3.toWei(float(BNBAmount), 'ether'),  # This is the Token(BNB) amount you want to Swap from
            'gas': gasAmount,
            'gasPrice': web3.toWei(gasPrice, 'gwei'),
            'nonce': nonce,
        })

        try:
            signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key)
            tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)  # BUY THE TOKEN
            print(style.GREEN + 'Transaction sent')
        except Exception as e:
            print(style.RED + " Transaction failed.")
            print(e)
            print("")  # line break: move onto scanning for next token


def Sell(tokenAddress, tokenContract, nonce):
    if (tokenAddress != None):
        print(style.GREEN + 'Selling token')

        tokenAmount = tokenContract.functions.balanceOf(walletAddress).call()
        tokenToBuy = web3.toChecksumAddress(tokenAddress)
        spend = web3.toChecksumAddress("0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c")  # wbnb contract address
        contract = web3.eth.contract(address=pancakeSwapRouterAddress, abi=pancakeABI)

        pancakeswap2_txn = contract.functions.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            # Set to 0 or specify min number of tokens - setting to 0 just buys X amount of token at its current price for whatever BNB specified
            [tokenToBuy, spend],
            walletAddress,
            (int(time.time()) + transactionRevertTime)
        ).buildTransaction({
            'from': walletAddress,
            'gas': gasAmount,
            'gasPrice': web3.toWei(gasPrice, 'gwei'),
            'nonce': nonce,
        })

        try:
            signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key)
            tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)  # BUY THE TOKEN
            print(style.GREEN + 'Transaction sent')
        except Exception as e:
            print(style.RED + " Transaction failed.")
            print(e)
            print("")  # line break: move onto scanning for next token


def approve(tokenContract, nonce):
    print(style.GREEN + 'Approving token')

    approve_tx = tokenContract.functions.approve(pancakeSwapRouterAddress, max_allowance).buildTransaction({
        'from': walletAddress,
        'nonce': nonce
    })

    signed_tx = web3.eth.account.sign_transaction(approve_tx, private_key)
    tx_hash = web3.eth.send_raw_transaction(signed_tx.rawTransaction)
    print(style.GREEN + 'Operation sent')



